import React, { useState, useEffect } from 'react';
import { Box, Button, CardMedia, IconButton } from '@mui/material';
import NavigateBeforeIcon from '@mui/icons-material/NavigateBefore';
import NavigateNextIcon from '@mui/icons-material/NavigateNext';

const Carousel = (images, height, width) => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
    }, 3000); // Change image every 3 seconds

    return () => clearInterval(intervalId); // Clean up the interval
  }, []);

  return (
    // <Box position="relative" maxWidth={650} sx={{my: 4}}>
    //   <Box display="flex" justifyContent="center" alignItems="center">
    //     {images.map((imageUrl, index) => (
    //       <Box key={index} display={index === currentImageIndex ? 'block' : 'none'}>
    //         <CardMedia component="img" image={imageUrl} alt={`Image ${index + 1}`} sx={{ maxWidth: '100%', maxHeight: '100%', objectFit: 'contain' }} />
    //       </Box>
    //     ))}
    //   </Box>
    // </Box>
    <Box position="relative" sx={{marginTop: "16px",}}>
    <Box display="flex" justifyContent="center" alignItems="center">
      {images.map((imageUrl, index) => (
        <Box key={index} display={index === currentImageIndex ? 'block' : 'none'} sx={{ maxHeight: height, width: width }}>
          <CardMedia component="img" image={imageUrl} alt={`Image ${index + 1}`} sx={{ maxWidth: '100%', maxHeight: '100%', objectFit: 'contain' }} />
        </Box>
      ))}
    </Box>
  </Box>
  );
};

export default Carousel;
