import React, { useState } from 'react';
import { Box, Button, CardMedia, IconButton } from '@mui/material';
import NavigateBeforeIcon from '@mui/icons-material/NavigateBefore';
import NavigateNextIcon from '@mui/icons-material/NavigateNext';

const images = [
  'https://images.unsplash.com/photo-1588880331179-bc9b93a8cb5e?q=80&w=2940&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D',
  'https://images.unsplash.com/photo-1677824410346-64daa536a611?q=80&w=2940&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D',
];

const Carousel = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const handleNext = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
  };

  const handlePrev = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === 0 ? images.length - 1 : prevIndex - 1));
  };

  return (
    <Box position="relative">
      <IconButton onClick={handlePrev} sx={{ position: 'absolute', top: '50%', left: 0, zIndex: 1 }}>
        <NavigateBeforeIcon />
      </IconButton>
      <IconButton onClick={handleNext} sx={{ position: 'absolute', top: '50%', right: 0, zIndex: 1 }}>
        <NavigateNextIcon />
      </IconButton>
      <Box display="flex" justifyContent="center" alignItems="center" width="1000px">
        {images.map((imageUrl, index) => (
          <Box key={index} display={index === currentImageIndex ? 'block' : 'none'}>
            <CardMedia component="img" image={imageUrl} alt={`Image ${index + 1}`} />
          </Box>
        ))}
      </Box>
    </Box>
  );
};

export default Carousel;
